import React, { Component } from 'react';
import {httpClient} from './../../utils/HttpClient';
import { server } from '../../constants';
import {connect} from 'react-redux';
import {register} from '../../actions/register.action';


class Register extends Component {
  constructor(props) {
    super(props)

    this.state = {
       username:"",
       password:""
    }
  }
  onClickRegister=()=>{
    httpClient.post(server.REGISTER_URL,this.state)
    .then(respone=>{alert(JSON.stringify(respone.data))})
    // Axios.post("http://localhost:8085/api/v2/authen/register",this.state)
    // .then(respone=>{alert(JSON.stringify(respone.data))})
  }
  render() {
    return (
      <div 
      className="login-box">
        <div className="login-logo">
          <a href="../../index2.html">
            <b>CMStock</b>Register
          </a>
        </div>
        {/* /.login-logo */}
        <div 
        style={{background:"whitesmoke",borderRadius:10}}
        className="login-box-body">
          <p className="login-box-msg">Sign in to start your session</p>
          <form >
            <div className="form-group has-feedback">
              <input
              onChange={e=>{this.setState({username:e.target.value})}}
              name="username"
                type="email"
                className="form-control"
                placeholder="Email"
              />
              <span className="glyphicon glyphicon-envelope form-control-feedback" />
            </div>
            <div className="form-group has-feedback">
              <input
              onChange={e=>{this.setState({password:e.target.value})}}
               name="password"
                type="password"
                className="form-control"
                placeholder="Password"
              />
              <span className="glyphicon glyphicon-lock form-control-feedback" />
            </div>
            {/* Register */}
            <div className="row">
              <div className="col-xs-12">
                <button
                onClick={this.onClickRegister}
                  className="btn btn-primary btn-block btn-flat"
                >
                  Register
                </button>
              </div>

              {/* /.col */}
            </div>     
              {/* cancel */}
            <div className="row">
              <div className="col-xs-12">
                <button
                onClick={(e)=>
                  {
                    e.preventDefault();
                    this.props.history.goBack()
                  }
                }
                style={{marginTop:8}}
                  className="btn btn-default btn-block"
                >
                  Cancel
                </button>
              </div>
              {/* /.col */}
            </div>        
          </form>
        </div>
        {/* /.login-box-body */}
      </div>
    );
  }
}

