import { HTTP_REGISTER_SUCCESS, HTTP_LOGIN_FETCHING, HTTP_LOGIN_FAILED } from "../constants";

const initialState = {
  result: null,
  isFetching: false,
  isError: false
};

export default (state = initialState, { type, payload }) => {
  switch (type) {
    case HTTP_LOGIN_FETCHING:
      return { ...state, result: null, isError: false, isFetching: true };
    case HTTP_REGISTER_SUCCESS:
      return { ...state, result: payload, isError: false, isFetching: false };
    case HTTP_LOGIN_FAILED:
      return { ...state, result: null, isError: true, isFetching: false };
    default:
      return state;
  }
};
